import React from 'react'
import "./Product.css"
import {useStateValue} from "./StateProvider"

function Goods({id, title, image, price, rating, descr = ''}) {

    const [{basket}, dispatch] = useStateValue();

    const addToBasket = () => {
        dispatch({
            type: 'ADD_TO_BASKET',
            item: {
                id: id,
                title: title,
                image: image,
                price: price,
                rating: rating,
            },
        });
    };

    return (
        <div className='product'>
            <div class="product_info" >
                <p className='product_title'>{title}</p>
                <p className='product_price'><small>$</small><strong>{price}</strong></p>
                <div class="product_rating">
                    {Array(rating)
                        .fill()
                        .map((_, i) => (
                            <p>‚≠ê</p>
                        ))}
                </div>
            </div>
            <img src={image} alt=""/>
            <div>
                {descr}
            </div>
            <button onClick={addToBasket} className="product_button">Add to Basket</button>
        </div>
    )
}

export default Goods
